package day03;

public class C07_PreDecrementPostDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int sayi=10;
sayi--;

System.out.println(sayi);
		
		
		int a=sayi--;
		
		System.out.println(a);	
		System.out.println(sayi);	
		
		System.out.println(--a);
		System.out.println(sayi);
		
		
		
		
		
		
		
		
	}

}
